mapper.select:
SELECT
    ID, USER_ID, QUESTION, ANSWER, MODE, CREATED_AT
FROM CHAT_MESSAGE
WHERE USER_ID = #{userId}
ORDER BY CREATED_AT DESC
    LIMIT #{limit}

mapper.delete:
DELETE FROM CHAT_MESSAGE
WHERE USER_ID = #{userId}
  AND ID NOT IN (
    SELECT ID
    FROM CHAT_MESSAGE
    WHERE USER_ID = #{userId}
    ORDER BY CREATED_AT DESC
    LIMIT #{keep}
    )

mapper.select:
SELECT HEALTH_DETAIL
FROM HEALTH_DATA
WHERE ORDER_ID = #{value}
